	/**
	 * 计算总页数
	 * @param:
	 * 	totalCount:总条数
	 * 	pageSize:分页大小
	 * */
	public static int totalPage(int totalCount,int pageSize) {
		int y = totalCount%pageSize;
		int z = totalCount/pageSize;
		return y > 0 ? z+1 : z;
	}
	
	public static List<String> getDateList(String date,int size,String format){
		List<String> dateList = new ArrayList<String>();
		SimpleDateFormat simpleDateFormat=new SimpleDateFormat(format);
		Date parseDate = null;
		if(StringUtils.isEmpty(date)) {
			parseDate = new Date();
		}else {
			try {
	            parseDate=simpleDateFormat.parse(date);
	        } catch(ParseException px) {
	            px.printStackTrace();
	        }
		}
		for(int i=0;i<size;i++) {
			dateList.add(simpleDateFormat.format(parseDate.getTime() - i * 24 * 60 * 60 * 1000));
		}
		return dateList;
	}
	
