	public static String queryString(Map<String,List<String>> paramterMap) {
		String result = "";
		if(!paramterMap.isEmpty()) {
			for(Map.Entry<String,List<String>> entry:paramterMap.entrySet()) {
				result += entry.getKey()+":"+String.join(" OR "+entry.getKey() + ":", entry.getValue());
				result += " AND ";
			}
			result = result.substring(0,result.length()-4);
			result = result.trim();
			result = "{\n" + 
					"          \"query_string\": {\n" + 
					"            \"query\": \""+result+"\",\n" + 
					"            \"analyze_wildcard\": true\n" + 
					"          }\n" + 
					"        }";
		}
		return result;
	}
	
